import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditEventForm() {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const [eventData, setEventData] = useState({ name: '', description: '', date: '' });

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/events/${eventId}`);
        setEventData({ name: response.data.name, description: response.data.description, date: response.data.date.slice(0, 10) });
      } catch (error) {
        console.error('Could not fetch event', error);
      }
    };

    fetchEvent();
  }, [eventId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/events/${eventId}`, eventData);
      alert('Event updated successfully!');
      navigate('/'); // Redirect to the event list
    } catch (error) {
      alert(`Failed to update event: ${error.message}`);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Edit Event</h2>
      {/* Inputs for editing name, description, and date */}
    </form>
  );
}

export default EditEventForm;
