import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

function EventList() {
  const [events, setEvents] = useState([]);
  const navigate = useNavigate(); // Initialize useNavigate for navigation

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Could not fetch events', error);
    }
  };

  const deleteEvent = async (eventId) => {
    try {
      await axios.delete(`http://localhost:5000/events/${eventId}`);
      alert('Event deleted successfully');
      setEvents(events.filter(event => event.id !== eventId));
    } catch (error) {
      console.error('Failed to delete event', error);
      alert('Failed to delete event');
    }
  };

  const handleEdit = (eventId) => {
    navigate(`/edit-event/${eventId}`); // Navigate to the edit page for this event
  };

  return (
    <div>
      <h2>Event List</h2>
      {events.length > 0 ? (
        <ul>
          {events.map((event) => (
            <li key={event.id}>
              {event.name} - {event.date} - {event.description}
              <button onClick={() => deleteEvent(event.id)} style={{ marginLeft: '10px' }}>Delete</button>
              <button onClick={() => handleEdit(event.id)} style={{ marginLeft: '10px' }}>Edit</button> {/* Add an Edit button here */}
            </li>
          ))}
        </ul>
      ) : (
        <p>No events found.</p>
      )}
    </div>
  );
}

export default EventList;
